import axios from "axios";

const API = axios.create({
  baseURL: "http://localhost:8088/api/", // Double-check the port for your backend API
  timeout: 5000, // Set a timeout in milliseconds for requests (optional)
});

// Error handling for all API calls
API.interceptors.response.use(
  (response) => response,
  (error) => {
    console.error("API Error:", error); // Log the error for debugging
    // Handle errors gracefully based on your application's needs
    // You can display user-friendly messages, redirect to an error page, etc.
    return Promise.reject(error);
  }
);

export const UserSignUp = async (data) => {
  try {
    const response = await API.post("/user/signup", data);
    return response.data; // Return the response data
  } catch (error) {
    // Handle signup errors appropriately
    console.error("Signup Error:", error);
    throw error; // Re-throw the error for further handling if needed
  }
};

export const UserSignIn = async (data) => {
  try {
    const response = await API.post("/user/signin", data);
    return response.data;
  } catch (error) {
    // Handle signin errors appropriately
    console.error("Signin Error:", error);
    throw error;
  }
};

export const getDashboardDetails = async (token) => {
  try {
    const response = await API.get("/user/dashboard", {
      headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
  } catch (error) {
    // Handle dashboard details retrieval errors
    console.error("Get Dashboard Details Error:", error);
    throw error;
  }
};

export const getWorkouts = async (token, date) => {
  try {
    const response = await API.get(`/user/workout${date}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
  } catch (error) {
    // Handle get workouts errors
    console.error("Get Workouts Error:", error);
    throw error;
  }
};

export const addWorkout = async (token, data) => {
  try {
    const response = await API.post(`/user/workout`, data, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return response.data;
  } catch (error) {
    // Handle add workout errors
    console.error("Add Workout Error:", error);
    throw error;
  }
};
